library(oce)
data(section)
Sflag <- section[['salinityFlag']]

## ------------------------------------------------------------------------
table(Sflag)

## ------------------------------------------------------------------------
S <- section[['salinity']]
T <- section[['temperature']]
theta <- section[['theta']]
p <- section[['pressure']]
Sflag <- section[['salinityFlag']]

## ------------------------------------------------------------------------
plot(S, Sflag)

## ------------------------------------------------------------------------
plotTS(as.ctd(as.ctd(S, T, p)))

## ------------------------------------------------------------------------
ok <- Sflag == 2
plotTS(as.ctd(as.ctd(S[ok], T[ok], p[ok])))

## ------------------------------------------------------------------------
section2 <- handleFlags(section)
plotTS(section2)

## ------------------------------------------------------------------------
plot(section, which="salinity")

## ------------------------------------------------------------------------
plot(section2, which="salinity")

